insert into PARAM_REAL values (  65,   1,   0,   0,   0,   3,  15,   0,  14,   1,                                0, '', '%.0f' , 'D', 'derived', 'scalar', '10^-6 arcsec', 'Post-Newtonian deflection angle, for an observer at one AU from the Sun, of a light ray arriving at right angles to the solar direction due to the spherically symmetric part of the gravitational field of the Sun', 'ESA, 1997, \'The Hipparcos and Tycho Catalogues\', ESA SP-1200, Volume 3, page 331; cf. S.A. Klioner, 2003, \'A Practical Relativistic Model for Microarcsecond Astrometry in Space\', AJ, 125, 1580, Equation 64', '1.0E6 * (1 + %PPN_Gamma%) * %Sun_GM% * 180 * 3600 / (%VelocityOfLight_Constant_Vacuum% * %VelocityOfLight_Constant_Vacuum% * %AstronomicalUnit_Meter% * %Pi_Constant%)', '', '2006-03-01T09:00:00');
insert into PARAM_REAL values (  66,   1,   0,   0,   0,   3,  15,   0,  15,   1,                                0, '', '%.0f' , 'D', 'derived', 'scalar', '10^-6 arcsec', 'Post-Newtonian deflection angle, for an observer at one AU from the Sun, of a Solar-limb-grazing light ray due to the spherically symmetric part of the gravitational field of the Sun', 'S.A. Klioner, 2003, \'A Practical Relativistic Model for Microarcsecond Astrometry in Space\', AJ, 125, 1580, Equation 64', '1.0E6 * 180 * 3600 * (1 + %PPN_Gamma%) * %Sun_GM% * cos(%Sun_EquatorialRadius% / (2 * %AstronomicalUnit_Meter%)) / (sin(%Sun_EquatorialRadius% / (2 * %AstronomicalUnit_Meter%)) * %VelocityOfLight_Constant_Vacuum% * %VelocityOfLight_Constant_Vacuum% * %AstronomicalUnit_Meter% * %Pi_Constant%)', '\\delta_{\\rm pN}', '2006-03-01T09:00:00');
