insert into PARAM_REAL values ( 131,   1,   0,   0,   0,  51,   0,   0,  22,   1,                        2451545.0, '', '%.1f' , 'D', 'basic'  , 'scalar', 'JD', 'Julian date of the standard epoch J2000 = J2000.0, i.e., calendar date 2000 January 1, 12:00:00 h = 2000-01-01T12:00:00 (TT)', 'Definition (e.g., ESA, 1997, \'The Hipparcos and Tycho Catalogues\', Volume 1, page 27)', '', 'J2000', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 132,   1,   0,   0,   0,  51,   0,   0,  23,   1,                                0, '', '%.1f' , 'D', 'derived', 'scalar', 'JD', 'Julian date of the Gaia-specific reference epoch J2010 = J2010.0 = JD 2455197.5 = 2010-01-01T00:00:00', 'U. Bastian, 5 July 2007, \'Reference systems, conventions, and notations for Gaia\', GAIA-CA-SP-ARI-BAS-003-06, issue 6, revision 1, version 5.0, Section 3.5 (formerly known as GAIA-ARI-BAS-003)', '%JulianDate_J2000% + (2010 - 2000) * %JulianYear_Day%', 'J2010', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 133,   1,   0,   0,   0,  52,   0,   0,  24,   1, 0, '1977-01-01T00:00:00 TAI, 1977-01-01T00:00:32.184 TCB, JD 2443144.5003725 TCB', '%s'   , 'S', 'basic'  , 'scalar', '', 'The origin of TCB is defined in terms of TAI: the reading of TCB on 1 January 1977, 00:00:00 TAI = JD 2443144.5 TAI must be 1 January 1977, 00:00:32.184 TCB = JD 2443144.5003725 TCB. This origin has been arbitrarily set so that TCB coincides with TT at the geocentre on 1 January 1977, 00:00:00 TAI', 'IAU, 1991, \'IAU Resolution A4, Recommendations 3 and 4\'. See, for example, M. Soffel, et al., 1 December 2003, \'The IAU 2000 resolutions for astrometry, celestial mechanics, and metrology in the relativistic framework: explanatory supplement\', AJ, 126, 2687-2706. See also http://www.iers.org/, \'Science background\', \'Recommendations\' (http://www.iers.org/MainDisp.csl?pid=98-133)', '', '', '2007-03-01T12:00:00');
insert into PARAM_REAL values ( 134,   1,   0,   0,   0,  52,   0,   0,  25,   1, 0, '1977-01-01T00:00:00 TAI, 1977-01-01T00:00:32.184 TCG, JD 2443144.5003725 TCG', '%s'   , 'S', 'basic'  , 'scalar', '', 'The origin of TCG is defined in terms of TAI: the reading of TCG on 1 January 1977, 00:00:00 TAI = JD 2443144.5 TAI must be 1 January 1977, 00:00:32.184 TCG = JD 2443144.5003725 TCG. This origin has been arbitrarily set so that TCG coincides with TT at the geocentre on 1 January 1977, 00:00:00 TAI', 'IAU, 1991, \'IAU Resolution A4, Recommendations 3 and 4\'. See, for example, M. Soffel, et al., 1 December 2003, \'The IAU 2000 resolutions for astrometry, celestial mechanics, and metrology in the relativistic framework: explanatory supplement\', AJ, 126, 2687-2706. See also http://www.iers.org/, \'Science background\', \'Recommendations\' (http://www.iers.org/MainDisp.csl?pid=98-133)', '', '', '2007-03-01T12:00:00');
