insert into PARAM_REAL values ( 165,   1,   0,   0,   0,  12,  29,   0,   0,   1,                                0, '', '%.3f' , 'D', 'derived', 'scalar', 'arcsec', 'Lunar diurnal parallax. Formally, this parameter is defined as the ratio of a fictitious mean equatorial radius of the Earth to the perturbed mean distance of the Moon; the ratio F_2 of the perturbed mean distance to the Moon (the perturbation being due to the Sun) to the two-body mean distance of the Moon (with the Sun not present and constant mean motion) equals 0.999093141975298 (see T.D. Moyer, 15 May 1971, \'Mathematical formulation of the Double-Precision Orbit Determination Programme (DPODP)\', NASA JPL Technical Report 32-1527, pages 25-26)', '', '180 * 3600 * asin(%Earth_EquatorialRadius% / %Moon_OrbitalSemiMajorAxis_J2000%) / %Pi_Constant%', '\\pi', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 166,   1,   0,   0,   0,  12,  30,   0,  22,   1,                        384400.E3, '', '%.6e' , 'D', 'basic'  , 'scalar', 'm', 'Semi-major axis of Lunar orbit (mean ecliptic orbital elements, at the standard epoch J2000.0, based on JPL\'s Planetary and Lunar Ephemerides DE405/LE405)', 'E.M. Standish, 2001, \'Approximate Mean Ecliptic Elements of the Lunar Orbit\', JPL IOM 312.F-01-004 (http://ssd.jpl.nasa.gov/?sat_elem)', '', 'a', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 167,   1,   0,   0,   0,  12,  31,   0,  22,   1,                           0.0554, '', '%.4f' , 'D', 'basic'  , 'scalar', '', 'Eccentricity of Lunar orbit (mean ecliptic orbital elements, at the standard epoch J2000.0, based on JPL\'s Planetary and Lunar Ephemerides DE405/LE405)', 'E.M. Standish, 2001, \'Approximate Mean Ecliptic Elements of the Lunar Orbit\', JPL IOM 312.F-01-004 (http://ssd.jpl.nasa.gov/?sat_elem)', '', 'e', '2006-03-01T09:00:00');
