insert into PARAM_REAL values ( 170,   1,   0,   0,   0,  12,  34,   0,  36,   1,                            5.997, '', '%.3f' , 'D', 'basic'  , 'scalar', 'yr', 'Precession period of the argument of periapsis of Lunar orbit, i.e., apsidal period (mean ecliptic orbital elements, at the standard epoch J2000.0, based on JPL\'s Planetary and Lunar Ephemerides DE405/LE405)', 'E.M. Standish, 2001, \'Approximate Mean Ecliptic Elements of the Lunar Orbit\', JPL IOM 312.F-01-004 (http://ssd.jpl.nasa.gov/?sat_elem)', '', '', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 171,   1,   0,   0,   0,  12,  34,   0,  37,   1,                           18.600, '', '%.3f' , 'D', 'basic'  , 'scalar', 'yr', 'Precession period of the longitude of the ascending node of Lunar orbit, i.e., nodal period (mean ecliptic orbital elements, at the standard epoch J2000.0, based on JPL\'s Planetary and Lunar Ephemerides DE405/LE405)', 'E.M. Standish, 2001, \'Approximate Mean Ecliptic Elements of the Lunar Orbit\', JPL IOM 312.F-01-004 (http://ssd.jpl.nasa.gov/?sat_elem)', '', '', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 172,   1,   0,   0,   0,  69,  12,   0,  38,   1,                           32.184, '', '%.3f' , 'D', 'basic'  , 'scalar', 's', 'Nominal value of the (constant) offset between TAI and TT: TT(TAI) = TAI + 32.184 s. This offset was chosen to give continuity with the previously used, but now obsolete, Ephemeris Time (see P.K. Seidelmann, 1992, \'Explanatory Supplement to the Astronomical Almanac\', University Science Books, Mill Valley, Ca., ISBN 0-935702-68-7). Deviations of this value, which are attributable to physical defects of atomic time standards, are probably between the limits \\pm 10 \\mus', 'ESA, 1997, \'The Hipparcos and Tycho Catalogues\', Volume 1, page 23', '', '{\\rm TT}-{\\rm TAI}', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 173,   1,   0,   0,   0,  70,  14,   0,   0,   1,                   3.986004418E14, '', '%.10e', 'D', 'basic'  , 'scalar', 'm^3 s^-2', 'Geocentric gravitational constant (TCB-compatible value in SI units), including the Earth\'s atmosphere but excluding the mass of the Moon', 'D.D. McCarthy, G. Petit, 1 May 2003, \'IERS Conventions (2003)\', U.S. Naval Observatory, Chapter 1, Table 1.1: \'IERS Numerical Standards\'. Note that the TCB-induced values 3.9860044415E14 and 3.9860044154E14 reported in Equation 4 and Table 3 in T. Fukushima, 2002, \'Report on Astronomical Constants\', Highlights of Astronomy, Volume 12, page 107 (Table 3: \'IAU 2000 File of Current Best Estimates\') are incorrect', '', 'GM_\\oplus', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 174,   1,   0,   0,   0,   5,  14,   0,   0,   1,                                0, '', '%.10e', 'D', 'derived', 'scalar', 'm^3 s^-2', 'Geocentric gravitational constant (TCB-compatible value in SI units), including the Earth\'s atmosphere but excluding the mass of the Moon', '', '%EarthEllipsoid_GM%', 'GM_\\oplus', '2006-03-01T09:00:00');
