insert into PARAM_REAL values ( 176,   1,   0,   0,   0,   5,  35,   0,  39,   1,                                0, '', '%.3f' , 'D', 'derived', 'scalar', 'g cm^-3', 'Mean mass density of the Earth. C.F. Yoder, 1995, \'Astrometric and Geodetic Properties of Earth and the solar System\', in \'Global Earth Physics; A Handbook of Physical Constants\', AGU Reference Shelf 1, American Geophysical Union, Table 2 (http://www.agu.org/reference/gephys/4_yoder.pdf), lists a value of 5.515 g cm^-3 for a mean volumetric radius of 6371.01 km and a mass of 5.9736E24 kg', '', '3 * %Earth_Mass% / (4 * %Pi_Constant% * %Earth_EquatorialRadius% * %Earth_EquatorialRadius% * %Earth_PolarRadius% * 1000)', '\\rho_\\oplus', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 177,   1,   0,   0,   0,  71,  36,   0,   0,   1,                                0, '', '%.6f' , 'D', 'derived', 'scalar', '', 'Ratio of Sun to Earth mass. The Earth mass includes the Earth\'s atmosphere but excludes the mass of the Moon', '', '%Sun_GM% / %Earth_GM%', '', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 178,   1,   0,   0,   0,  70,  37,   0,  40,   1,                     1.0826359E-3, '', '%.8e' , 'D', 'basic'  , 'scalar', '', 'Dynamical form-factor of the Earth, i.e., second-degree zonal harmonic of the geopotential including the indirect tidal distortion on J_2, i.e., in the zero-frequency-tide system JGM-3 (see D.D. McCarthy, G. Petit, 1 May 2003, \'IERS Conventions (2003)\', Chapter 1; http://maia.usno.navy.mil/conv2000.html)', 'T. Fukushima, 2002, \'Report on Astronomical Constants\', Highlights of Astronomy, Volume 12, page 107 (Table 3: \'IAU 2000 File of Current Best Estimates\'). Numerical value is zero-frequency-tide value from E. Groten, 2000, \'Report of Special Commission 3 of IAG\', in Proc. of IAU Coll. 180, \'Towards models and constants for sub-microarcsecond astrometry\`, eds K.J. Johnston, D.D. McCarthy, B.J. Luzum, G.H. Kaplan., page 337', '', 'J_{2\\oplus}', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 179,   1,   0,   0,   0,  70,  38,   0,  40,   1,                       6378136.62, '', '%.2f' , 'D', 'basic'  , 'scalar', 'm', 'Semi-major axis of the Earth reference ellipsoid (zero-frequency-tide value; see D.D. McCarthy, G. Petit, 1 May 2003, \'IERS Conventions (2003)\', Chapter 1; http://maia.usno.navy.mil/conv2000.html)', 'T. Fukushima, 2002, \'Report on Astronomical Constants\', Highlights of Astronomy, Volume 12, page 107 (Table 3: \'IAU 2000 File of Current Best Estimates\'). Numerical value is zero-frequency-tide value from E. Groten, 2000, \'Report of Special Commission 3 of IAG\', in Proc. of IAU Coll. 180, \'Towards models and constants for sub-microarcsecond astrometry\`, eds K.J. Johnston, D.D. McCarthy, B.J. Luzum, G.H. Kaplan., page 337', '', 'a_\\oplus', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 180,   1,   0,   0,   0,   5,  39,   0,   0,   1,                                0, '', '%.8e' , 'D', 'derived', 'scalar', 'm', 'Equatorial radius of the Earth', '', '%EarthEllipsoid_SemiMajorAxis_ZeroFrequencyTide%', 'a_\\oplus', '2006-03-01T09:00:00');
