insert into PARAM_REAL values ( 182,   1,   0,   0,   0,   5,  41,   0,   0,   1,                                0, '', '%.6e' , 'D', 'derived', 'scalar', 'm', 'Mean volumetric radius of the Earth. Note that a value of 6371.01E3 km is listed in C.F. Yoder, 1995, \'Astrometric and Geodetic Properties of Earth and the solar System\', in \'Global Earth Physics; A Handbook of Physical Constants\', AGU Reference Shelf 1, American Geophysical Union, Table 2 (http://www.agu.org/reference/gephys/4_yoder.pdf)', '', '%Earth_EquatorialRadius% * pow(1 - %Earth_Flattening%, 1/3)', 'R_{V\\oplus}', '2006-03-01T09:00:00');
