insert into PARAM_REAL values ( 311,   1,   0,   0,   0,   3,  39,   0,  56,   1,                           959.63, '', '%.2f' , 'D', 'basic'  , 'scalar', 'arcsec', 'Solar apparent (equatorial) radius at unit distance (value following extrapolation to infinite Fried parameter in the derivative method, corrected for atmospheric and darkening effects)', 'F. Chollet, V. Sinceac, 1999, \'Analysis of solar radius determination obtained by the modern CCD astrolabe of the Calern Observatory; a new approach of the solar limb definition\', A&AS, 139, 219', '', '', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 312,   1,   0,   0,   0,   3,  39,   0,   0,   1,                                0, '', '%.9e' , 'D', 'derived', 'scalar', 'm', 'Solar (equatorial) radius (photosphere)', '', '%AstronomicalUnit_Meter% * %Pi_Constant% * %Sun_EquatorialRadius_Apparent% / (3600 * 180)', 'R_\\odot', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 313,   1,   0,   0,   0,   3,  35,   0,  39,   1,                                0, '', '%.3f' , 'D', 'derived', 'scalar', 'g cm^-3', 'Mean solar mass density', '', '3 * %Sun_Mass% / (4 * %Pi_Constant% * %Sun_EquatorialRadius% * %Sun_EquatorialRadius% * %Sun_EquatorialRadius% * 1000)', '\\rho_\\odot', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 314,   1,   0,   0,   0,  14,  60,   0,   0,   1,                             3.10, '', '%.2f' , 'D', 'basic'  , 'scalar', '', 'Ratio of total to selective absorption (typical value). One has: R_V = A_V / E(B-V), where A_V is the total extinction in the Johnson V band and E(B-V) is the colour excess (see also J.H.J. de Bruijne, 26 May 2003, \'Stellar fluxes: transformations and calibrations\', GAIA-JdB-005, revision 1)', 'J.A. Cardelli, G.C. Clayton, J.S. Mathis, 1989, \'The relationship between infrared, optical, and ultraviolet extinction\', ApJ, 345, 245', '', 'R_V', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 315,   1,   0,   0,   0,  14,  61,   0,  57,   1,                              1.0, '', '%.1f' , 'D', 'basic'  , 'scalar', 'mag kpc^-1', 'Typical extinction in the Johnson V band (A_V) per kpc in the Galactic plane; values ranging from 0.5 to 1.5 mag kpc^-1 are considered \'normal\' (e.g., H. Jonch-Sorensen, 1994, \'CCD uvby-beta photometry of faint stars. 2: Reddening in six fields in the Galaxy\', A&A, 292, 92)', 'Typical value (\'common lore\')', '', 'A_V', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 316,   1,   0,   0,   0,  13,  62,   0,   0,   1,                            550.0, '', '%.1f' , 'D', 'basic'  , 'scalar', 'nm', 'Reference wavelength at which the flux f_{0\\lambda} of an unreddened A0V star is calibrated (see also J.H.J. de Bruijne, 26 May 2003, \'Stellar fluxes: transformations and calibrations\', GAIA-JdB-005, revision 1)', 'V. Straizys, 1992, \'Multicolor stellar photometry\', Pachart Publ.\ House (Tucson), Table 21', '', '\\lambda_0', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 317,   1,   0,   0,   0,  13,  63,   0,   0,   1,                             0.00, '', '%.2f' , 'D', 'basic'  , 'scalar', 'mag', 'Johnson V minus Gaia G (V-G) magnitude of an unreddened A0V star, applicable to any photometric band G', 'Definition of Gaia G band(s)', '', '(V-G)_0', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 318,   1,   0,   0,   0,  13,  64,   0,   0,   1,                            -0.01, '', '%.2f' , 'D', 'basic'  , 'scalar', 'mag', 'Johnson V minus Cousins R (V-R) magnitude of an unreddened A0V star. The passbands are taken from M.S. Bessell, 1990, \'UBVRI passbands\', PASP, 102, 1181', 'V. Straizys, 1992, \'Multicolor stellar photometry\', Pachart Publ.\ House (Tucson), Table 27', '', '(V-R)_0', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 319,   1,   0,   0,   0,  13,  65,   0,   0,   1,                            -0.02, '', '%.2f' , 'D', 'basic'  , 'scalar', 'mag', 'Johnson V minus Cousins I (V-I) magnitude of an unreddened A0V star. The passbands are taken from M.S. Bessell, 1990, \'UBVRI passbands\', PASP, 102, 1181', 'V. Straizys, 1992, \'Multicolor stellar photometry\', Pachart Publ.\ House (Tucson), Table 27', '', '(V-I)_0', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 320,   1,   0,   0,   0,  13,  66,   0,   9,   1,                         3.66E-11, '', '%.3e' , 'D', 'basic'  , 'scalar', 'W m^-2 nm^-1', 'Flux f_{0\\lambda} of an unreddened A0V star with V = 0 mag at the wavelength \\lambda_0 (see also J.H.J. de Bruijne, 26 May 2003, \'Stellar fluxes: transformations and calibrations\', GAIA-JdB-005, revision 1)', 'V. Straizys, 1992, \'Multicolor stellar photometry\', Pachart Publ.\ House (Tucson), Table 21', '', 'f_{0\\lambda}(\\lambda_0)', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 321,   1,   0,   0,   0,  13,  66,   0,   1,   1,                                0, '', '%.4e' , 'D', 'derived', 'scalar', 'W m^-2 Hz^-1', 'Flux f_{0\\nu} of an unreddened A0V star with V = 0 mag at the wavelength \\lambda_0 (see also J.H.J. de Bruijne, 26 May 2003, \'Stellar fluxes: transformations and calibrations\', GAIA-JdB-005, revision 1)', '', '1.0E-9 * %A0VStar_CalibrationWavelength% * %A0VStar_CalibrationWavelength% * %A0VStar_CalibrationFlux_Lambda% / %VelocityOfLight_Constant_Vacuum%', 'f_{0\\nu}(\\lambda_0)', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 322,   1,   0,   0,   0,  13,  66,   0,   7,   1,                                0, '', '%.0f' , 'D', 'derived', 'scalar', 'photons s^-1 m^-2 nm^-1', 'Photon flux N_{0\\lambda}(\\lambda_0) of an unreddened A0V star with V = 0 mag at the wavelength \\lambda_0 (see also J.H.J. de Bruijne, 26 May 2003, \'Stellar fluxes: transformations and calibrations\', GAIA-JdB-005, revision 1). Note that the parameter A0VStar_Spectrum_NumberOfPhotons refers to Pickles\' star number 009, and not to the Kurucz/ATLAS9 Vega spectrum which has been used for flux normalisation/calibration and zero-point definition; the parameter A0VStar_Spectrum_NumberOfPhotons therefore does not precisely have A0VStar_CalibrationFlux photons s^-1 m^-2 nm^-1 at \\lambda_0 = A0VStar_CalibrationWavelength', '', '%A0VStar_CalibrationFlux_Nu% / (%Planck_Constant% * %A0VStar_CalibrationWavelength%)', 'N_{0\\lambda}(\\lambda_0)', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 323,   1,   0,   0,   0,  13,   7,   0,  58,   1,                       8827168169, '', '%.0f' , 'D', 'basic'  , 'scalar', 'photons s^-1 m^-2', 'Calibration (function) normalisation constant N_0. This constant can be used to define the zero point of the Johnson V magnitude scale by imposing the requirement that, for any stellar photon flux density N_\\lambda (in photons s^-1 m^-2 nm^-1 above the Earth\'s atmosphere) with V = 0 mag, the integral from 470 to 700 nm (the support interval of the Johnson V band) of N_\\lambda times S_V(\\lambda) equals N_0 photons s^-1 m^-2. The function S_V(\\lambda) and the normalisation constant N_0 depend on the definition of the shape of the Johnson V band, on the monochromatic calibration flux f_{0\\lambda} (or f_{0\\nu}) at \\lambda_0, and on the spectrum f_{0\\nu}(\\lambda) of the general unreddened A0V star', 'J.H.J. de Bruijne, 26 May 2003, \'Stellar fluxes: transformations and calibrations\', GAIA-JdB-005, revision 1, Appendix D', '', 'N_0', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 324,   1,   3,   0,   0,   3,  37,   0,   0,   1,                           2.9E-7, '', '%.2e' , 'D', 'basic'  , 'scalar', '', 'Dynamical form-factor of the Sun (Stokes\' second-degree zonal harmonic of the solar potential; DE410 value)', 'E.M. Standish, 24 April 2003, \'JPL Planetary Ephemeris DE410\', JPL IOM 312.N-03-009 (Table III)', '', 'J_{2\\odot}', '2007-08-17T10:00:00');
insert into PARAM_REAL values ( 325,   1,   0,   0,   0,  99,  13,   0,   0,   1,                                0, '', '%.3e' , 'D', 'derived', 'scalar', 'kg', 'Mass of 3 Juno', 'Value is calculated following J.L. Hilton, 1999, \'US Naval Observatory Ephemerides of the Largest Asteroids\', AJ, 117, 1077, who assumes a mean mass density of 3 g cm^-3. A mass of 2.0E19 kg is found on http://nssdc.gsfc.nasa.gov/planetary/factsheet/asteroidfact.html', '4 * %Pi_Constant% * (%Asteroid3Juno_Diameter% / 2) * (%Asteroid3Juno_Diameter% / 2) * (%Asteroid3Juno_Diameter% / 2) * 3 * 1.0E12 / 3', 'M', '2006-03-01T09:00:00');
