insert into PARAM_REAL values ( 391,   1,   0,   0,   0, 101,  35,   0,  39,   1,                                0, '', '%.3f' , 'D', 'derived', 'scalar', 'g cm^-3', 'Mean mass density of Charon', '', '3 * %Charon_Mass% / (4 * %Pi_Constant% * %Charon_Radius% * %Charon_Radius% * %Charon_Radius% * 1000)', '\\rho', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 392,   1,   0,   0,   0,  50,  39,   0,   0,   1,                          1151.E3, '', '%.4e' , 'D', 'basic'  , 'scalar', 'm', 'Equatorial radius of Pluto', 'P.K. Seidelmann, 1992, \'Explanatory Supplement to the Astronomical Almanac\', University Science Books, Mill Valley, Ca., ISBN 0-935702-68-7, Table 15.8 on page 706', '', 'a', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 393,   1,   0,   0,   0,  50,  41,   0,   0,   1,                                0, '', '%.4e' , 'D', 'derived', 'scalar', 'm', 'Mean volumetric radius of Pluto', '', '%Pluto_EquatorialRadius% * pow(1 - %Pluto_Flattening%, 1/3)', 'R_V', '2006-03-01T09:00:00');
