insert into PARAM_REAL values ( 395,   1,   0,   0,   0,  50,  69,   0,   0,   1,                             -1.0, '', '%.1f' , 'D', 'basic'  , 'scalar', 'mag', 'V(1,0) magnitude of Pluto (i.e, the visual magnitude of the planet reduced to a distance of 1 AU from both the Sun and Earth and phase angle zero). This parameter is also refered to as absolute magnitude in planetary sciences', 'P.K. Seidelmann, 1992, \'Explanatory Supplement to the Astronomical Almanac\', University Science Books, Mill Valley, Ca., ISBN 0-935702-68-7, Table 15.8 on page 706', '', 'V(1,0)', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 396,   1,   0,   0,   0,  50,  68,   0,   0,   1,                              0.3, '', '%.1f' , 'D', 'basic'  , 'scalar', '', 'Geometric albedo of Pluto (i.e, the ratio of the illumination at the Earth from the planet for zero phase angle to the illumination produced by a Lambertian surface of the same radius as the planet placed at the same position)', 'P.K. Seidelmann, 1992, \'Explanatory Supplement to the Astronomical Almanac\', University Science Books, Mill Valley, Ca., ISBN 0-935702-68-7, Table 15.8 on page 706', '', '', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 397,   1,   0,   0,   0,  50,  35,   0,  39,   1,                                0, '', '%.3f' , 'D', 'derived', 'scalar', 'g cm^-3', 'Mean mass density of Pluto (rough estimate)', '', '3 * %Pluto_Mass% / (4 * %Pi_Constant% * %Pluto_EquatorialRadius% * %Pluto_EquatorialRadius% * %Pluto_EquatorialRadius% * 1000)', '\\rho', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 398,   1,   0,   0,   0, 102,   0,   0,  50,   1,                                0, '', '%.15e', 'D', 'derived', 'scalar', 'deg', 'One radian in units of degrees', '', '180 / %Pi_Constant%', '', '2006-11-20T16:00:00');
