insert into PARAM_REAL values ( 406,   1,   0,   0,   0, 107,   0,   0,  63,   1,                                0, '', '%.11f', 'D', 'derived', 'scalar', 'deg^2', 'One steradian in units of square degrees', '', '360 * 360 / (4 * %Pi_Constant% * %Pi_Constant%)', '', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 407,   1,   0,   0,   0, 108,   0,   0,  63,   1,                                0, '', '%.10f', 'D', 'derived', 'scalar', 'deg^2', 'Surface area of unit sphere (4 Pi steradians) in units of square degrees', '', '360 * 360 / %Pi_Constant%', '', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 408,   1,   0,   0,   0,   2,  70,   0,  64,   1,                             22.5, '', '%.1f' , 'D', 'basic'  , 'scalar', 'mag arcsec^-2', 'Johnson V band sky-background surface brightness seen from space; average value', 'ESA, 16 November 2006, \'Gaia mission requirements document (MRD)\', issue 2, revision 0, GAIA-EST-RD-00553, Requirement SCI-090. See also ESA, July 2000, \'Gaia; Composition, Formation and Evolution of the Galaxy; Concept and Technology Study Report\', ESA-SCI(2000)4, Section 6.4.3, page 239', '', '', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 409,   1,   0,   0,   0,   2,  70,   0,  65,   1,                             21.0, '', '%.1f' , 'D', 'basic'  , 'scalar', 'mag arcsec^-2', 'Johnson V band sky-background surface brightness seen from space; worst-case value (in the ecliptic)', 'ESA, July 2000, \'Gaia; Composition, Formation and Evolution of the Galaxy; Concept and Technology Study Report\', ESA-SCI(2000)4, Section 6.4.3, page 239', '', '', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 410,   1,   0,   0,   0,   2,  71,   0,  64,   1,                                0, '', '%.0f' , 'D', 'derived', 'scalar', 'stars deg^-2', 'Sky-averaged density of stars with G <= 20.00 mag. Note that a value of 25.1E3 stars deg^-2 is given in ESA, July 2000, \'Gaia; Composition, Formation and Evolution of the Galaxy; Concept and Technology Study Report\', ESA-SCI(2000)4, Section 6.4.5 and Table 6.6, pages 240-242', 'See also ESA, 16 November 2006, \'Gaia mission requirements document (MRD)\', issue 2, revision 0, GAIA-EST-RD-00553, Section 4.1', '%Sky_NumberOfStars_Total% / %FourPiSteradian_DegreeSquare%', 'N_{20}', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 411,   1,   0,   0,   0,   2,  71,   0,  57,   1,                           150000, '', '%.0f' , 'D', 'basic'  , 'scalar', 'stars deg^-2', 'Typical density of stars with G <= 20.00 mag. This value is a typical value encountered in the Galactic plane and, as such, is more representative than the sky-averaged value', 'J.H.J. de Bruijne, 31 October 2003, \'PDHE load assumptions: properties of the sky\', GAIA-JdB-009. See also ESA, 16 November 2006, \'Gaia mission requirements document (MRD)\', issue 2, revision 0, GAIA-EST-RD-00553, Section 4.1 and ESA, July 2000, \'Gaia; Composition, Formation and Evolution of the Galaxy; Concept and Technology Study Report\', ESA-SCI(2000)4, Section 3.6.6, page 176', '', '', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 412,   1,   0,   0,   0,   2,  71,   0,  66,   1,                           600000, '', '%.0f' , 'D', 'basic'  , 'scalar', 'stars deg^-2', 'Typical density of stars with G <= 20.00 mag below which Gaia is designed to operate nominally: nominal observations and all astrometric requirements shall be achieved in the two superimposed fields of view computed using the design object density in one instrument field of view plus the typical object density in the other instrument field of view (Requirement SCI-150). Requirement SCI-160 guarantees that a strategy to observe high-density sky regions (e.g., Baade\'s window), with stellar densities up to the worst-case star density, will be implemented (if higher densities than the worst-case density are encountered, the brightest objects up to the worst-case density will be observed). One option under investigation is to have several successive transits of the same sky region at a reduced precession rate using a modified scanning law (MSL; see EADS-Astrium, October 2005, \'Gaia: Proposal for the Implementation Phase\', Volume II \'Technical Proposal\', Part 1 \'Proposed Spacecraft Design and Development Approach\', Chapter 3 \'From Mission Requirements to Spacecraft Concept\', Section 3.3.4 \'The Modified Scanning Law\', page 22 and L. Lindegren, 7 April 2005, \'Multi-pass scanning across Baade\`s Window\', GAIA-LL-058)', 'ESA, 16 November 2006, \'Gaia mission requirements document (MRD)\', issue 2, revision 0, GAIA-EST-RD-00553, Section 4.1, Requirements SCI-150 and SCI-160', '', '', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 413,   1,   0,   0,   0,   2,  71,   0,  65,   1,                              3E6, '', '%.1e' , 'D', 'basic'  , 'scalar', 'stars deg^-2', 'Density of stars with G <= 20.00 mag (worst-case, localised value, e.g., in Baade\'s window)', 'ESA, 16 November 2006, \'Gaia mission requirements document (MRD)\', issue 2, revision 0, GAIA-EST-RD-00553, Section 4.1. See also ESA, July 2000, \'Gaia; Composition, Formation and Evolution of the Galaxy; Concept and Technology Study Report\', ESA-SCI(2000)4, Section 6.5.2, page 244', '', '', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 414,   1,   0,   0,   0,   2,  71,   0,  67,   1, 0, '101.6, 79.8, 31.2, 11.4, 3.8', '%s', 'E5', 'basic', 'scalar', '1E3 stars deg^-2', 'Density of stars with G <= 20.00 mag in the Galactic-latitude ranges 0-5 deg, 5-10 deg, 10-20 deg, 20-30 deg, and 30-90 deg', 'ESA, July 2000, \'Gaia; Composition, Formation and Evolution of the Galaxy; Concept and Technology Study Report\', ESA-SCI(2000)4, Section 6.4.5 and Table 6.6, pages 240-242', '', '', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 415,   1,   0,   0,   0,   2,  71,   0,  68,   1,                              6.0, '', '%.1f' , 'D', 'derived', 'scalar', '', 'The brighter star-selection limiting magnitude of RVS compared to SM/AF/BP/RP (G_RVS (= C1M861 = RVF) = 17.00 mag versus G = 20.00 mag, respectively; see parameters Selection_Magnitude_Maximum) is assumed to correspond to a factor 6 reduction in star density and the number of stars', 'D. Katz, M. Cropper, J.M. Desert, et al., 12 January 2006, \'CU6 - Spectroscopic processing - preliminary functional and data-flow analysis\', GAIA-C6-TN-OPM-DK-001-2, Section 3.1, page 15', '%Sky_NumberOfStars_Total% / 172E6', '', '2006-03-07T11:00:00');
insert into PARAM_REAL values ( 416,   1,   0,   0,   0,   2,  72,   0,  69,   1,                              779, '', '%.0f' , 'D', 'basic'  , 'scalar', '10^6 stars', 'Predicted number of observed stars (i.e., G <= 20.00 mag) in the disk (main sequence stars plus white dwarfs)', 'ESA, July 2000, \'Gaia; Composition, Formation and Evolution of the Galaxy; Concept and Technology Study Report\', ESA-SCI(2000)4, Section 6.4.4 and Table 6.3, pages 239-240 (Galaxy model from J. Torra, et al., 1999, \'Predicting Gaia Observations from a Star-Count Model\', Baltic Astronomy, 8, 171 and extinction law from J. Hakkila, et al., 1997, \'A Computerised Model of Large-Scale Visual Interstellar Extinction\', AJ, 114, 2043)', '', '', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 417,   1,   0,   0,   0,   2,  72,   0,  70,   1,                               92, '', '%.0f' , 'D', 'basic'  , 'scalar', '10^6 stars', 'Predicted number of observed stars (i.e., G <= 20.00 mag) in the disk (giants)', 'ESA, July 2000, \'Gaia; Composition, Formation and Evolution of the Galaxy; Concept and Technology Study Report\', ESA-SCI(2000)4, Section 6.4.4 and Table 6.3, pages 239-240 (Galaxy model from J. Torra, et al., 1999, \'Predicting Gaia Observations from a Star-Count Model\', Baltic Astronomy, 8, 171 and extinction law from J. Hakkila, et al., 1997, \'A Computerised Model of Large-Scale Visual Interstellar Extinction\', AJ, 114, 2043)', '', '', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 418,   1,   0,   0,   0,   2,  72,   0,  71,   1,                               97, '', '%.0f' , 'D', 'basic'  , 'scalar', '10^6 stars', 'Predicted number of observed stars (i.e., G <= 20.00 mag) in the thick disk', 'ESA, July 2000, \'Gaia; Composition, Formation and Evolution of the Galaxy; Concept and Technology Study Report\', ESA-SCI(2000)4, Section 6.4.4 and Table 6.3, pages 239-240 (Galaxy model from J. Torra, et al., 1999, \'Predicting Gaia Observations from a Star-Count Model\', Baltic Astronomy, 8, 171 and extinction law from J. Hakkila, et al., 1997, \'A Computerised Model of Large-Scale Visual Interstellar Extinction\', AJ, 114, 2043)', '', '', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 419,   1,   0,   0,   0,   2,  72,   0,  72,   1,                               67, '', '%.0f' , 'D', 'basic'  , 'scalar', '10^6 stars', 'Predicted number of observed stars (i.e., G <= 20.00 mag) in the spheroid (including the bulge)', 'ESA, July 2000, \'Gaia; Composition, Formation and Evolution of the Galaxy; Concept and Technology Study Report\', ESA-SCI(2000)4, Section 6.4.4 and Table 6.3, pages 239-240 (Galaxy model from J. Torra, et al., 1999, \'Predicting Gaia Observations from a Star-Count Model\', Baltic Astronomy, 8, 171 and extinction law from J. Hakkila, et al., 1997, \'A Computerised Model of Large-Scale Visual Interstellar Extinction\', AJ, 114, 2043)', '', '', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 420,   1,   0,   0,   0,   2,  72,   0,  73,   1,                                0, '', '%.4e' , 'D', 'derived', 'scalar', 'stars', 'Predicted total number of observed stars (i.e., G <= 20.00 mag)', 'See also ESA, 16 November 2006, \'Gaia mission requirements document (MRD)\', issue 2, revision 0, GAIA-EST-RD-00553, Requirement SCI-130', '1.0E6 * (%Sky_NumberOfStars_DiskMSPlusWD% + %Sky_NumberOfStars_DiskGiant% + %Sky_NumberOfStars_ThickDisk% + %Sky_NumberOfStars_Spheroid%)', '', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 421,   1,   0,   0,   0,   2,  72,   0,  74,   1, 0, '47.467, 68.520, 97.807, 137.896, 192.293, 265.430, 362.918, 494.105, 671.507, 911.189, 1233.517, 1666.488, 2245.837', '%.3f' , 'E13', 'basic', 'scalar', '10^6 stars', 'Cumulative number of stars, integrated over the full sky, as function of G magnitude for the following limits: up to G = 15.0 mag, up to G = 15.5 mag, ..., up to G = 20.5 mag, and up to G = 21.0 mag (numerical values from parameter Sky_ObjectDensity)', 'The data used to produce the standard Gaia Galaxy model have been provided by the teams of the Observatoire de Besancon (A. Robin, C. Reyle, et al.) and the Observatorio Astronomico di Torino (R. Drimmel, et al.); the harmonisation has been provided by the SWG (X. Luri). See U. Lammers, 22 April 2005, \'Gaia Standard Galaxy Model Access Software (GSGMAS); User Guide\', GAIA-UL-010, Rev. 1.0. See also R. Drimmel, et al., 19 July 2005, \'Recommendations on the use of estimated star counts for Gaia studies\', SWG-RD-004', '', '', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 422,   1,   0,   0,   0,   2,  72,   0,  75,   1, 0, '107.694, 153.909, 217.348, 303.568, 420.325, 579.498, 799.686, 1019.873, 1240.061, 1460.248, 1680.435, 1900.623, 2120.810', '%.3f' , 'E13', 'basic', 'scalar', '10^6 stars', 'Cumulative number of stars, integrated over the full sky, as function of G_RVS (= C1M861 = RVF) magnitude for the following limits: up to G_RVS = 15.0 mag, up to G_RVS = 15.5 mag, ..., up to G_RVS = 20.5 mag, and up to G_RVS = 21.0 mag (numerical values from parameter Sky_ObjectDensity)', 'The data used to produce the standard Gaia Galaxy model have been provided by the teams of the Observatoire de Besancon (A. Robin, C. Reyle, et al.) and the Observatorio Astronomico di Torino (R. Drimmel, et al.); the harmonisation has been provided by the SWG (X. Luri). See U. Lammers, 22 April 2005, \'Gaia Standard Galaxy Model Access Software (GSGMAS); User Guide\', GAIA-UL-010, Rev. 1.0. See also R. Drimmel, et al., 19 July 2005, \'Recommendations on the use of estimated star counts for Gaia studies\', SWG-RD-004', '', '', '2007-08-13T16:00:00');
insert into PARAM_REAL values ( 423,   1,   0,   0,   0, 109,  12,   0,   0,   1,                                0, '', '%.10e' , 'D', 'derived', 'scalar', 'kg', 'Atomic mass constant (also known as atomic mass unit [amu]; 1 amu is defined as 1/12-th of the mass of a 12-C atom). Note: best-measured value equals 1.660538782E-27 (P.J. Mohr, B.N. Taylor, D.B. Newell, 7 March 2007, \'The 2006 CODATA Recommended Values of the Fundamental Physical Constants\', National Institute of Standards and Technology, Gaithersburg, MD 20899-8401; http://www.codata.org/ and http://physics.nist.gov/constants, Web Version 5.0)', '', '1.0E-3 / %Avogadro_Constant%', 'm_u', '2007-08-30T11:00:00');
insert into PARAM_REAL values ( 424,   1,   0,   0,   0, 110,  12,   0,   0,   1,                                0, '', '%.8e' , 'D', 'derived', 'scalar', 'J K^-1', 'Boltzmann\'s constant. Note: best-measured value equals 1.3806504E-23 (P.J. Mohr, B.N. Taylor, D.B. Newell, 7 March 2007, \'The 2006 CODATA Recommended Values of the Fundamental Physical Constants\', National Institute of Standards and Technology, Gaithersburg, MD 20899-8401; http://www.codata.org/ and http://physics.nist.gov/constants, Web Version 5.0)', '', '%MolarGas_Constant% / %Avogadro_Constant%', 'k', '2007-08-30T11:00:00');
insert into PARAM_REAL values ( 425,   1,   0,   0,   0, 111,  12,   0,   0,   1,                                0, '', '%.7e' , 'D', 'derived', 'scalar', 'W m^-2 K^-4', 'Stefan-Boltzmann constant. Note: best-measured value equals 5.670400E-8 (P.J. Mohr, B.N. Taylor, D.B. Newell, 7 March 2007, \'The 2006 CODATA Recommended Values of the Fundamental Physical Constants\', National Institute of Standards and Technology, Gaithersburg, MD 20899-8401; http://www.codata.org/ and http://physics.nist.gov/constants, Web Version 5.0)', '', '(%Pi_Constant% * %Pi_Constant% / 60) * pow(%Boltzmann_Constant%, 4) * pow(2 * %Pi_Constant%, 3) * pow(%Planck_Constant%, -3) * pow(%VelocityOfLight_Constant_Vacuum%, -2)', '\\sigma', '2007-08-30T11:00:00');
insert into PARAM_REAL values ( 426,   1,   0,   0,   0, 112,  12,   0,  76,   1,                                0, '', '%.9e' , 'D', 'derived', 'scalar', 'W m^2', 'First radiation constant. Note: best-measured value equals 3.74177118E-16 (P.J. Mohr, B.N. Taylor, D.B. Newell, 7 March 2007, \'The 2006 CODATA Recommended Values of the Fundamental Physical Constants\', National Institute of Standards and Technology, Gaithersburg, MD 20899-8401; http://www.codata.org/ and http://physics.nist.gov/constants, Web Version 5.0)', '', '2 * %Pi_Constant% * %Planck_Constant% * %VelocityOfLight_Constant_Vacuum% * %VelocityOfLight_Constant_Vacuum%', 'c_1', '2007-08-30T11:00:00');
insert into PARAM_REAL values ( 427,   1,   0,   0,   0, 112,  12,   0,  26,   1,                                0, '', '%.8e' , 'D', 'derived', 'scalar', 'm K', 'Second radiation constant. Note: best-measured value equals 1.4387752E-2 (P.J. Mohr, B.N. Taylor, D.B. Newell, 7 March 2007, \'The 2006 CODATA Recommended Values of the Fundamental Physical Constants\', National Institute of Standards and Technology, Gaithersburg, MD 20899-8401; http://www.codata.org/ and http://physics.nist.gov/constants, Web Version 5.0)', '', '%Planck_Constant% * %VelocityOfLight_Constant_Vacuum% / %Boltzmann_Constant%', 'c_2', '2007-08-30T11:00:00');
insert into PARAM_REAL values ( 428,   1,   0,   0,   0, 113,  12,   0,   0,   1,                                0, '', '%.11e', 'D', 'derived', 'scalar', '', 'Fine structure constant. Note: best-measured value equals 7.2973525376E-3 (P.J. Mohr, B.N. Taylor, D.B. Newell, 7 March 2007, \'The 2006 CODATA Recommended Values of the Fundamental Physical Constants\', National Institute of Standards and Technology, Gaithersburg, MD 20899-8401; http://www.codata.org/ and http://physics.nist.gov/constants, Web Version 5.0)', '', '(%ElementaryCharge_Constant% * %ElementaryCharge_Constant% * 2 * %Pi_Constant%) / (4 * %Pi_Constant% * %Electric_Constant% * %Planck_Constant% * %VelocityOfLight_Constant_Vacuum%)', '\\alpha', '2007-08-30T11:00:00');
