insert into PARAM_REAL values ( 430,   1,   0,   0,   0, 115,  12,   0,   0,   1,                                0, '', '%.6f' , 'D', 'derived', 'scalar', 'm^-1', 'Rydberg constant. Note: best-measured value equals 10973731.568527 (P.J. Mohr, B.N. Taylor, D.B. Newell, 7 March 2007, \'The 2006 CODATA Recommended Values of the Fundamental Physical Constants\', National Institute of Standards and Technology, Gaithersburg, MD 20899-8401; http://www.codata.org/ and http://physics.nist.gov/constants, Web Version 5.0)', '', '%FineStructure_Constant% * %FineStructure_Constant% * %Electron_Mass% * %VelocityOfLight_Constant_Vacuum% / (2 * %Planck_Constant%)', 'R_{\\infty}', '2007-08-30T11:00:00');
insert into PARAM_REAL values ( 431,   1,   0,   0,   0, 116,  12,   0,   0,   1,                                0, '', '%.11e', 'D', 'derived', 'scalar', 'm', 'Bohr radius. Note: best-measured value equals 0.52917720859E-10 (P.J. Mohr, B.N. Taylor, D.B. Newell, 7 March 2007, \'The 2006 CODATA Recommended Values of the Fundamental Physical Constants\', National Institute of Standards and Technology, Gaithersburg, MD 20899-8401; http://www.codata.org/ and http://physics.nist.gov/constants, Web Version 5.0)', '', '%FineStructure_Constant% / (4 * %Pi_Constant% * %Rydberg_Constant%)', 'a_0', '2007-08-30T11:00:00');
