insert into PARAM_REAL values ( 545,   1,   0,   0,   0,   3,  97,   0,   0,   1,                                0, '', '%.1f' , 'D', 'derived', 'scalar', 'm s^-2', 'Surface gravity of the Sun', '', '%Sun_GM% / (%Sun_EquatorialRadius% * %Sun_EquatorialRadius%)', 'g_\\odot', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 546,   1,   0,   0,   0,   3,  98,   0,  12,   1,                             1371, '', '%.0f' , 'D', 'basic'  , 'scalar', 'W m^-2', 'Energy flux of the Sun at a distance of one astronomical unit (also refered to as solar constant). Due to orbital modulation, this value changes by about \\pm 3.4% during a year; this value changes by 0.04% during a solar cycle (variations are thought to be \\pm 0.1% around the mean in European Cooperation for Space Standardisation (ECSS), 17 April 2001, \'6. Solar and Earth electromagnetic radiation and indices\', http://www.spenvis.oma.be/spenvis/ecss/ecss06/ecss06.html); during solar maximum, sunspots can induce 0.25% variations during one rotation', 'C.H. Duncan, et al., 1982, \'Latest rocket measurements of the solar constant\', Solar Energy, 28(5), 385 (1982SoEn...28..385D). The value 1371 W m^-2 is the straight mean of the three values measured by Duncan et al.: 1367, 1372, and 1374 W m^-2', '', 'J_\\odot\ (1\ AU)', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 547,   1,   0,   0,   0,   3,  99,   0,   0,   1,                                0, '', '%.4e' , 'D', 'derived', 'scalar', 'W', 'Luminosity of the Sun. This value changes by 0.04% during a solar cycle (variations are thought to be \\pm 0.1% around the mean in European Cooperation for Space Standardisation (ECSS), 17 April 2001, \'6. Solar and Earth electromagnetic radiation and indices\', http://www.spenvis.oma.be/spenvis/ecss/ecss06/ecss06.html); during solar maximum, sunspots can induce 0.25% variations during one rotation', '', '4 * %Pi_Constant% * %AstronomicalUnit_Meter% * %AstronomicalUnit_Meter% * %Sun_EnergyFlux_AstronomicalUnit%', 'L_\\odot', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 548,   1,   0,   0,   0,   3, 100,   0,   0,   1,                                0, '', '%.0f' , 'D', 'derived', 'scalar', 'K', 'Effective (black-body) temperature of the Sun', '', 'pow(%Sun_Luminosity% / (4 * %Pi_Constant% * %StefanBoltzmann_Constant% * %Sun_EquatorialRadius% * %Sun_EquatorialRadius%), 1/4)', 'T_{{\\rm eff}\\odot}', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 549,   1,   0,   0,   0,   3,  98,   0,  80,   1,                                0, '', '%.0f' , 'D', 'derived', 'scalar', 'W m^-2', 'Energy flux of the Sun at L2. Due to orbital modulation, this value changes by about \\pm 3.4% during a year; this value changes by 0.04% during a solar cycle (variations are thought to be \\pm 0.1% around the mean in European Cooperation for Space Standardisation (ECSS), 17 April 2001, \'6. Solar and Earth electromagnetic radiation and indices\', http://www.spenvis.oma.be/spenvis/ecss/ecss06/ecss06.html); during solar maximum, sunspots can induce 0.25% variations during one rotation. A value of 1400 W m^-2 is assumed in EADS-Astrium, 27 June 2002, \'Gaia System-Level Technical Reassessment Study (SLTRS); Final report\', EF5/FR/PC/038.02, page 91', '', '%Sun_Luminosity% / (4 * %Pi_Constant% * %AstronomicalUnit_Meter% * (1 + %L2_Rho%) * %AstronomicalUnit_Meter% * (1 + %L2_Rho%))', 'J_\\odot\ (L2)', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 550,   1,   0,   0,   0,   3, 101,   0,  12,   1,                                0, '', '%.3e' , 'D', 'derived', 'scalar', 'Pa', 'Mean value of the solar-radiation pressure at a distance of one astronomical unit. Due to orbital modulation, this value changes by about \\pm 3.4% during a year; this value changes by 0.04% during a solar cycle (variations are thought to be \\pm 0.1% around the mean in European Cooperation for Space Standardisation (ECSS), 17 April 2001, \'6. Solar and Earth electromagnetic radiation and indices\', http://www.spenvis.oma.be/spenvis/ecss/ecss06/ecss06.html); during solar maximum, sunspots can induce 0.25% variations during one rotation. A value of 9.02E-6 Pa is given in European Cooperation for Space Standardisation (ECSS), 17 April 2001, \'6. Solar and Earth electromagnetic radiation and indices\'', '', '%Sun_EnergyFlux_AstronomicalUnit% / %VelocityOfLight_Constant_Vacuum%', '', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 551,   1,   0,   0,   0,   3, 101,   0,  80,   1,                                0, '', '%.3e' , 'D', 'derived', 'scalar', 'Pa', 'Mean value of the solar-radiation pressure at L2. Due to orbital modulation, this value changes by about \\pm 3.4% during a year; this value changes by 0.04% during a solar cycle (variations are thought to be \\pm 0.1% around the mean in European Cooperation for Space Standardisation (ECSS), 17 April 2001, \'6. Solar and Earth electromagnetic radiation and indices\', http://www.spenvis.oma.be/spenvis/ecss/ecss06/ecss06.html); during solar maximum, sunspots can induce 0.25% variations during one rotation. A value of 4.66E-6 Pa is assumed in EADS-Astrium, 27 June 2002, \'Gaia System-Level Technical Reassessment Study (SLTRS); Final report\', EF5/FR/PC/038.02, page 44 (cf. page 91). A value of 4.56E-6 Pa is used in M. Hechler, 20 July 2003, \'Gaia mission analysis: Orbits around L2 with radiation pressure\', Section 2, page 1', '', '%Sun_EnergyFlux_L2% / %VelocityOfLight_Constant_Vacuum%', '', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 552,   1,   0,   0,   0,   3, 102,   0,  80,   1,                                0, '', '%.3e' , 'D', 'derived', 'scalar', 'Pa', 'Standard deviation of the incoherent noise (integrated white noise) in the solar-radiation pressure at L2', 'EADS-Astrium, 27 June 2002, \'Gaia System-Level Technical Reassessment Study (SLTRS); Final report\', EF5/FR/PC/038.02, page 44', '4.5E-6 * %Sun_RadiationPressure_L2%', '', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 553,   1,   0,   0,   0,   3, 103,   0,  80,   1,                                0, '', '%.3e' , 'D', 'derived', 'scalar', 'Pa', 'Amplitude of the 5-minute solar oscillation', 'EADS-Astrium, 27 June 2002, \'Gaia System-Level Technical Reassessment Study (SLTRS); Final report\', EF5/FR/PC/038.02, page 44', '3.0E-6 * %Sun_RadiationPressure_L2%', '', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 554,   1,   0,   0,   0, 137,  12,   0,  81,   1,                                0, '', '%.6f' , 'D', 'derived', 'scalar', 's yr^-1', 'Precession constant m = p cos(\\epsilon_0), in s per Julian year, at the standard epoch J2000.0. The precession in right ascension \\alpha equals m + n sin(\\alpha) tan(\\delta); the precession in declination \\delta equals n cos(\\alpha)', '', '%PrecessionLongitude_Constant_J2000% * cos(%Pi_Constant% * %ObliquityOfEcliptic_J2000% / (180 * 3600)) / (15 * 100)', 'm', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 555,   1,   0,   0,   0, 137,  12,   0,  82,   1,                                0, '', '%.6f' , 'D', 'derived', 'scalar', 'arcsec yr^-1', 'Precession constant n = p sin(\\epsilon_0), in arcsec per Julian year, at the standard epoch J2000.0. The precession in right ascension \\alpha equals m + n sin(\\alpha) tan(\\delta); the precession in declination \\delta equals n cos(\\alpha)', '', '%PrecessionLongitude_Constant_J2000% * sin(%Pi_Constant% * %ObliquityOfEcliptic_J2000% / (180 * 3600)) / 100', 'n', '2006-03-01T09:00:00');
