insert into PARAM_REAL values ( 601,   1,   0,   0,   0, 152,  35,   0,   0,   1,                             3.16, '', '%.2f' , 'D', 'basic'  , 'scalar', 'g cm^-3', 'Density of SiC (Boostec)', 'F. Safa, 23 April 2002, \'SiC versus C-SiC trade-off\', EADS-Astrium-SAS (Toulouse), pages 8-9 (document in Livelink). Note that a value of 3.14 g cm^-3 is listed in EADS-Astrium, 3 October 2003, \'Astro primary mirror design report; DDR status\', GAIAMIR.RP.00018.T.ASTR, issue 2, revision 1, page 24. See also EADS-Astrium, October 2005, \'Gaia: Proposal for the Implementation Phase\', Volume II \'Technical Proposal\', Part 1 \'Proposed Spacecraft Design and Development Approach\', Chapter 7 \'Payload Module Design\', Section 7.8 \'PLM Mechanical Design\', page 2', '', '\\rho', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 602,   1,   0,   0,   0, 152, 108,   0,   0,   1,                              420, '', '%.0f' , 'D', 'basic'  , 'scalar', 'GPa', 'Young\'s modulus of SiC (Boostec); a measure for the resistance of a material to elastic (recoverable) deformation under load. Also known as elastic modulus and tension modulus', 'F. Safa, 23 April 2002, \'SiC versus C-SiC trade-off\', EADS-Astrium-SAS (Toulouse), pages 8-9 (document in Livelink). See also EADS-Astrium, October 2005, \'Gaia: Proposal for the Implementation Phase\', Volume II \'Technical Proposal\', Part 1 \'Proposed Spacecraft Design and Development Approach\', Chapter 7 \'Payload Module Design\', Section 7.8 \'PLM Mechanical Design\', page 2', '', 'E', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 603,   1,   0,   0,   0, 152, 109,   0,   0,   1,                                0, '', '%.0f' , 'D', 'derived', 'scalar', '10^6 N m kg^-1', 'Specific stiffness of SiC (Boostec); also known as specific modulus', 'F. Safa, 23 April 2002, \'SiC versus C-SiC trade-off\', EADS-Astrium-SAS (Toulouse), pages 8-9 (document in Livelink). See also EADS-Astrium, October 2005, \'Gaia: Proposal for the Implementation Phase\', Volume II \'Technical Proposal\', Part 1 \'Proposed Spacecraft Design and Development Approach\', Chapter 7 \'Payload Module Design\', Section 7.8 \'PLM Mechanical Design\', page 2', '%SiC_YoungModulus% / %SiC_MassDensity%', 'E/\\rho', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 604,   1,   0,   0,   0, 152, 110,   0,   0,   1,                             0.16, '', '%.2f' , 'D', 'basic'  , 'scalar', '', 'Poisson ratio of SiC (Boostec)', 'F. Safa, 23 April 2002, \'SiC versus C-SiC trade-off\', EADS-Astrium-SAS (Toulouse), pages 8-9 (document in Livelink). Note that a value of 0.17 is listed in EADS-Astrium, 3 October 2003, \'Astro primary mirror design report; DDR status\', GAIAMIR.RP.00018.T.ASTR, issue 2, revision 1, page 24. See also EADS-Astrium, October 2005, \'Gaia: Proposal for the Implementation Phase\', Volume II \'Technical Proposal\', Part 1 \'Proposed Spacecraft Design and Development Approach\', Chapter 7 \'Payload Module Design\', Section 7.8 \'PLM Mechanical Design\', page 2', '', '\\nu', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 605,   1,   0,   0,   0, 152, 111,   0,  64,   1,                              390, '', '%.0f' , 'D', 'basic'  , 'scalar', '10^6 Pa', 'Average strength of SiC (Boostec) in the four-point bending test', 'F. Safa, 23 April 2002, \'SiC versus C-SiC trade-off\', EADS-Astrium-SAS (Toulouse), pages 8-9 (document in Livelink). Note that a lower limit of 300 MPa is listed in EADS-Astrium, 3 October 2003, \'Astro primary mirror design report; DDR status\', GAIAMIR.RP.00018.T.ASTR, issue 2, revision 1, page 24. See also EADS-Astrium, October 2005, \'Gaia: Proposal for the Implementation Phase\', Volume II \'Technical Proposal\', Part 1 \'Proposed Spacecraft Design and Development Approach\', Chapter 7 \'Payload Module Design\', Section 7.8 \'PLM Mechanical Design\', page 2', '', '\\sigma', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 606,   1,   0,   0,   0, 152, 112,   0,  86,   1,                               12, '', '%.0f' , 'D', 'basic'  , 'scalar', '', 'The Weibull modulus of fired/sintered SiC (Boostec). The Weibull modulus is the slope of the linear plot of ln(ln(P^-1)) versus ln(\\sigma), where P is the probability of survival under a given stress \\sigma. The numerical value is a lower limit for the Weibull modulus', 'F. Safa, 23 April 2002, \'SiC versus C-SiC trade-off\', EADS-Astrium-SAS (Toulouse), pages 8-9 (document in Livelink). See also EADS-Astrium, October 2005, \'Gaia: Proposal for the Implementation Phase\', Volume II \'Technical Proposal\', Part 1 \'Proposed Spacecraft Design and Development Approach\', Chapter 7 \'Payload Module Design\', Section 7.8 \'PLM Mechanical Design\', page 2', '', 'M', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 607,   1,   0,   0,   0, 152, 112,   0,  87,   1,                               10, '', '%.0f' , 'D', 'basic'  , 'scalar', '', 'The Weibull modulus of machined SiC (Boostec). The Weibull modulus is the slope of the linear plot of ln(ln(P^-1)) versus ln(\\sigma), where P is the probability of survival under a given stress \\sigma. The numerical value is a lower limit for the Weibull modulus', 'F. Safa, 23 April 2002, \'SiC versus C-SiC trade-off\', EADS-Astrium-SAS (Toulouse), pages 8-9 (document in Livelink). See also EADS-Astrium, October 2005, \'Gaia: Proposal for the Implementation Phase\', Volume II \'Technical Proposal\', Part 1 \'Proposed Spacecraft Design and Development Approach\', Chapter 7 \'Payload Module Design\', Section 7.8 \'PLM Mechanical Design\', page 2', '', 'M', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 608,   1,   0,   0,   0, 152, 113,   0,  26,   1,                                0, '', '%.0f' , 'D', 'derived', 'scalar', 'GPa', 'Second Lame constant of SiC (Boostec), also known as shear modulus or rigidity', '', '%SiC_YoungModulus% / (2 * (1 + %SiC_PoissonRatio%))', '\\mu', '2006-03-01T09:00:00');
insert into PARAM_REAL values ( 609,   1,   0,   0,   0, 152, 113,   0,  76,   1,                                0, '', '%.0f' , 'D', 'derived', 'scalar', 'GPa', 'First Lame constant of SiC (Boostec)', '', '%SiC_PoissonRatio% * %SiC_YoungModulus% / ((1 + %SiC_PoissonRatio%) * (1 - 2 * %SiC_PoissonRatio%))', '\\lambda', '2006-03-01T09:00:00');
