insert into PARAM_REAL values ( 735,   1,   0,   0,   0,  67, 135,   0, 111,   1,                                0, '', '%.1f' , 'D', 'derived', 'scalar', '10^-6 arcsec yr^-1', 'Maximum (change in) aberration brought about by the acceleration of the LSR relative to the Galactic centre (resulting, if not corrected for, in spurious (apparent) proper motions for extra-Galactic sources in some regions of the sky)', 'J. Kovalevsky, 2003, \'Aberration in proper motions\', A&A, 404, 743. See also ESA, July 2000, \'Gaia; Composition, Formation and Evolution of the Galaxy; Concept and Technology Study Report\', ESA-SCI(2000)4, Section 1.8.10, page 110', '%LSR_CircularVelocity% * 1.0E3 * %LSR_CircularVelocity% * 1.0E3 * %JulianYear_Day% * %Day_Second% * %Radian_Arcsecond% * 1.0E6 / (%VelocityOfLight_Constant_Vacuum% * 1.0E3 * %LSR_GalactocentricRadius% * %Parsec_Meter%)', '', '2006-03-01T09:00:00');
